const axios = require('axios');

async function testImageAnalysis() {
    try {
        // A small 1x1 white pixel base64 jpeg
        const base64Image = "data:image/jpeg;base64,";

        console.log("Sending image analysis request...");
        const response = await axios.post('http://localhost:5000/api/ai/analyze-image', {
            image: base64Image
        });
        
        console.log("Image Success:", response.data);
    } catch (error) {
        console.error("Image Failed:", error.response ? error.response.data : error.message);
    }
}

async function testChat() {
    console.log("\nTesting Chat...");
    try {
        const response = await axios.post('http://localhost:5000/api/ai/chat', {
            message: "Hello"
        });
        console.log("Chat Success:", response.data);
    } catch (error) {
        console.error("Chat Failed:", error.response ? error.response.data : error.message);
    }
}

(async () => {
    await testImageAnalysis();
    await testChat();
})();
